mapper.select:
select id,vid,sid,content
from message
where vid =#{vid,jdbcType=INTEGER}

select id,vid,sid,content
from message
where vid = #{vid,jdbcType=INTEGER} and sid =#{sid,jdbcType=INTEGER}

mapper.insert:
insert into message ( vid, sid,content
  )
values (#{vid,jdbcType=INTEGER}, #{sid,jdbcType=INTEGER}, 
  #{content,jdbcType=VARCHAR}
  )
