mapper.insert:
insert into score (id, sid, tid,score
  )
values (#{id,jdbcType=INTEGER}, #{sid,jdbcType=INTEGER}, #{tid,jdbcType=INTEGER}, 
  #{score,jdbcType=DECIMAL}
  )

mapper.select:
select id, sid, tid,score
from score
where sid = #{sid,jdbcType=INTEGER} and tid =#{tid,jdbcType=INTEGER}

select id, sid, tid,score
from score
where tid =#{tid,jdbcType=INTEGER}
